mapper.select:
SELECT q1.orderId as orderId, q1.orderStatus as orderStatus, q1.associatedId as associatedId, q1.dealAmount as dealAmount, q1.actualAmount as actualAmount, q1.orderType as orderType, q1.orderQr as orderQr, q1.retain1 as retain1, q1.orderAccount as orderAccount, q1.externalOrderId as externalOrderId, q1.createTime as createTime, q1.orderQrUser as orderQrUser, q1.payImg as payImg FROM alipay_deal_order q1 WHERE q1.orderQrUser = #{userId} and to_days(q1.createTime) = to_days(#{createTime})
and q1.orderStatus = #{orderStatus}
and q1.orderType = #{orderType} order by createTime desc

SELECT q1.orderId as orderId, q1.orderStatus as orderStatus, q1.associatedId as associatedId, q1.dealAmount as dealAmount, q1.actualAmount as actualAmount, q1.orderType as orderType, q1.retain1 as retain1, q1.orderQr as orderQr, q1.orderAccount as orderAccount, q1.externalOrderId as externalOrderId, q1.createTime as createTime, q1.orderQrUser as orderQrUser, q1.orderQrUser as orderQrUser FROM alipay_deal_order q1 LEFT JOIN alipay_file_list q2 ON q1.orderQr = q2.concealId WHERE 1=1
and q1.orderQrUser = #{orderQrUser}
and q1.retain1 = #{retain1}
and to_days(q1.createTime) = to_days(#{time}) order by createTime desc
